    98	// Note: the key_caches and value_caches vectors are constant but
    99	// not the Tensors they contain. The vectors need to be const refs
   100	// in order to satisfy pytorch's C++ operator registration code.
   101	void copy_blocks(std::vector<torch::Tensor> const& key_caches,
   102	                 std::vector<torch::Tensor> const& value_caches,
   103	                 const torch::Tensor& block_mapping) {
   104	  int num_layers = key_caches.size();
   105	  TORCH_CHECK(num_layers == value_caches.size());
   106	  if (num_layers == 0) {
   107	    return;
   108	  }
   109	  torch::Device cache_device = key_caches[0].device();
   110	  TORCH_CHECK(cache_device.is_cuda());
   111	
   112	  // Create data structures for the kernel.
   113	  // Create an array of pointers to the key and value caches.
   114	  int64_t key_cache_ptrs[num_layers];
   115	  int64_t value_cache_ptrs[num_layers];
   116	  for (int layer_idx = 0; layer_idx < num_layers; ++layer_idx) {
   117	    key_cache_ptrs[layer_idx] =
   118	        reinterpret_cast<int64_t>(key_caches[layer_idx].data_ptr());
   119	    value_cache_ptrs[layer_idx] =
   120	        reinterpret_cast<int64_t>(value_caches[layer_idx].data_ptr());
   121	  }
   122	
   123	  // block_mapping is a 2D tensor with shape (num_pairs, 2).
   124	  int num_pairs = block_mapping.size(0);
   125	
   126	  // Move the data structures to the GPU.
   127	  // NOTE: This synchronizes the CPU and GPU.
   128	  torch::Tensor key_cache_ptrs_tensor =
   129	      torch::from_blob(key_cache_ptrs, {num_layers}, torch::kInt64)
   130	          .to(cache_device);
   131	  torch::Tensor value_cache_ptrs_tensor =
   132	      torch::from_blob(value_cache_ptrs, {num_layers}, torch::kInt64)
   133	          .to(cache_device);
   134	
   135	  // Launch the kernel.
   136	  const int numel_per_block = key_caches[0][0].numel();
   137	  dim3 grid(num_layers, num_pairs);
   138	  dim3 block(std::min(1024, numel_per_block));
   139	  const at::cuda::OptionalCUDAGuard device_guard(cache_device);
   140	  const cudaStream_t stream = at::cuda::getCurrentCUDAStream();
   141	  VLLM_DISPATCH_FLOATING_AND_BYTE_TYPES(
   142	      key_caches[0].scalar_type(), "copy_blocks_kernel", ([&] {
   143	        vllm::copy_blocks_kernel<scalar_t><<<grid, block, 0, stream>>>(
   144	            key_cache_ptrs_tensor.data_ptr<int64_t>(),
   145	            value_cache_ptrs_tensor.data_ptr<int64_t>(),
   146	            block_mapping.data_ptr<int64_t>(), numel_per_block);
   147	      }));
   148	}
