   263	void reshape_and_cache(
   264	    torch::Tensor& key,    // [num_tokens, num_heads, head_size]
   265	    torch::Tensor& value,  // [num_tokens, num_heads, head_size]
   266	    torch::Tensor&
   267	        key_cache,  // [num_blocks, num_heads, head_size/x, block_size, x]
   268	    torch::Tensor&
   269	        value_cache,  // [num_blocks, num_heads, head_size, block_size]
   270	    torch::Tensor& slot_mapping,  // [num_tokens] or [num_actual_tokens]
   271	    const std::string& kv_cache_dtype, const double k_scale,
   272	    const double v_scale) {
   273	  // NOTE(woosuk): In vLLM V1, key.size(0) can be different from
   274	  // slot_mapping.size(0) because of padding for CUDA graphs.
   275	  // In vLLM V0, key.size(0) is always equal to slot_mapping.size(0) because
   276	  // both include padding.
   277	  // In vLLM V1, however, key.size(0) can be larger than slot_mapping.size(0)
   278	  // since key includes padding for CUDA graphs, while slot_mapping does not.
   279	  // In this case, slot_mapping.size(0) represents the actual number of tokens
   280	  // before padding.
   281	  // For compatibility with both cases, we use slot_mapping.size(0) as the
   282	  // number of tokens.
   283	  int num_tokens = slot_mapping.size(0);
   284	  if (num_tokens == 0) {
   285	    return;
   286	  }
   287	  int num_heads = key.size(1);
   288	  int head_size = key.size(2);
   289	  int block_size = key_cache.size(3);
   290	  int x = key_cache.size(4);
   291	
   292	  int key_stride = key.stride(0);
   293	  int value_stride = value.stride(0);
   294	
