   381	void convert_fp8(torch::Tensor& dst_cache, torch::Tensor& src_cache,
   382	                 const double scale, const std::string& kv_cache_dtype) {
   383	  torch::Device src_device = src_cache.device();
   384	  torch::Device dst_device = dst_cache.device();
   385	  TORCH_CHECK(src_device.is_cuda(), "src must be on a GPU")
   386	  TORCH_CHECK(dst_device.is_cuda(), "dst must be on a GPU")
   387	  TORCH_CHECK(src_device.index() == dst_device.index(),
   388	              "src and dst must be on the same GPU");
   389	  at::cuda::OptionalCUDAGuard device_guard(src_device);
   390	
   391	  int64_t num_blocks = src_cache.size(0);
   392	  int64_t block_stride = src_cache.stride(0);
   393	
   394	  dim3 grid(num_blocks);
   395	  dim3 block(std::min(block_stride, int64_t(512)));
   396	  const cudaStream_t stream = at::cuda::getCurrentCUDAStream();
   397	
   398	  if (kv_cache_dtype == "auto") {
   399	    if (src_cache.dtype() == at::ScalarType::Float) {
   400	      CALL_CONVERT_FP8(uint8_t, float, vllm::Fp8KVCacheDataType::kAuto);
   401	    } else if (src_cache.dtype() == at::ScalarType::Half) {
   402	      CALL_CONVERT_FP8(uint8_t, uint16_t, vllm::Fp8KVCacheDataType::kAuto);
   403	    } else if (src_cache.dtype() == at::ScalarType::BFloat16) {
   404	      CALL_CONVERT_FP8(uint8_t, __nv_bfloat16, vllm::Fp8KVCacheDataType::kAuto);
   405	    } else if (dst_cache.dtype() == at::ScalarType::Float) {
   406	      CALL_CONVERT_FP8(float, uint8_t, vllm::Fp8KVCacheDataType::kAuto);
   407	    } else if (dst_cache.dtype() == at::ScalarType::Half) {
   408	      CALL_CONVERT_FP8(uint16_t, uint8_t, vllm::Fp8KVCacheDataType::kAuto);
   409	    } else if (dst_cache.dtype() == at::ScalarType::BFloat16) {
