    24	void swap_blocks(torch::Tensor& src, torch::Tensor& dst,
    25	                 const torch::Tensor& block_mapping) {
    26	  torch::Device src_device = src.device();
    27	  torch::Device dst_device = dst.device();
    28	  cudaMemcpyKind memcpy_type;
    29	  if (src_device.is_cuda() && dst_device.is_cuda()) {
    30	    TORCH_CHECK(src_device.index() == dst_device.index(),
    31	                "src and dst must be on the same GPU");
    32	    memcpy_type = cudaMemcpyDeviceToDevice;
    33	  } else if (src_device.is_cuda() && dst_device.is_cpu()) {
    34	    memcpy_type = cudaMemcpyDeviceToHost;
    35	  } else if (src_device.is_cpu() && dst_device.is_cuda()) {
    36	    memcpy_type = cudaMemcpyHostToDevice;
    37	  } else {
    38	    TORCH_CHECK(false, "Invalid device combination");
    39	  }
    40	
    41	  // NOTE(youkaichao): keep in mind that `block_mapping` should be
    42	  // a cpu tensor, otherwise every `item` call will require a gpu-cpu
    43	  // synchronization.
    44	  TORCH_CHECK(block_mapping.device().is_cpu(), "block_mapping must be on CPU");
    45	
    46	  char* src_ptr = static_cast<char*>(src.data_ptr());
    47	  char* dst_ptr = static_cast<char*>(dst.data_ptr());
    48	
    49	  const int64_t block_size_in_bytes = src.element_size() * src[0].numel();
    50	  const at::cuda::OptionalCUDAGuard device_guard(
    51	      src_device.is_cuda() ? src_device : dst_device);
    52	  const cudaStream_t stream = at::cuda::getCurrentCUDAStream();
    53	  // NOTE(woosuk): This can be slow if the number of blocks is large.
    54	  const int64_t num_blocks = block_mapping.size(0);
