import time
import json
import torch
import importlib


def call_alibi_bias(mod, alibi_slopes, dtype, seq_lens, device):
    try:
        return mod._make_alibi_bias(alibi_slopes, dtype, seq_lens, device)
    except TypeError:
        return mod._make_alibi_bias(alibi_slopes, dtype, seq_lens)


def call_sliding_bias(mod, seq_lens, window_size, dtype, device):
    try:
        return mod._make_sliding_window_bias(seq_lens, window_size, dtype, device)
    except TypeError:
        return mod._make_sliding_window_bias(seq_lens, window_size, dtype)


def bench_once(num_heads=32, seq_lens=(256, 384, 512, 768, 1024), window_size=256):
    mod = importlib.import_module('vllm.attention.backends.torch_sdpa')
    device = torch.device('cpu')
    dtype = torch.float32
    alibi_slopes = torch.rand(num_heads, dtype=dtype, device=device)

    # Warmup
    for _ in range(3):
        out1 = call_alibi_bias(mod, alibi_slopes, dtype, list(seq_lens), device)
        out2 = call_sliding_bias(mod, list(seq_lens), window_size, dtype, device)
        # consume to avoid DCE
        _ = sum(t.sum().item() for t in out1) + sum(t.sum().item() for t in out2)

    iters = 20

    t0 = time.perf_counter()
    acc = 0.0
    for _ in range(iters):
        out1 = call_alibi_bias(mod, alibi_slopes, dtype, list(seq_lens), device)
        acc += sum(t.sum().item() for t in out1)
    t1 = time.perf_counter()

    t2 = time.perf_counter()
    for _ in range(iters):
        out2 = call_sliding_bias(mod, list(seq_lens), window_size, dtype, device)
        acc += sum(t.sum().item() for t in out2)
    t3 = time.perf_counter()

    return {
        'alibi_bias_ms': (t1 - t0) * 1000.0 / iters,
        'sliding_bias_ms': (t3 - t2) * 1000.0 / iters,
        'acc': acc,
    }


if __name__ == '__main__':
    torch.set_num_threads(max(1, torch.get_num_threads()))
    res = bench_once()
    print(json.dumps(res, indent=2))

