import time
import torch


def bench_alloc(iters: int = 1000, shape=(256, 256)):
    # Warmup
    for _ in range(100):
        _ = torch.empty(shape)
        _ = torch.zeros(1)

    t0 = time.time()
    for _ in range(iters):
        _ = torch.zeros(shape)
    t1 = time.time()

    for _ in range(iters):
        _ = torch.empty(shape)
    t2 = time.time()

    print({
        'iters': iters,
        'shape': shape,
        'zeros_s': t1 - t0,
        'empty_s': t2 - t1,
        'speedup': (t1 - t0) / max(1e-9, (t2 - t1))
    })


if __name__ == '__main__':
    bench_alloc()

