   122	/* Generic fused_add_rms_norm_kernel
   123	   The width field is not used here but necessary for other specializations.
   124	 */
   125	template <typename scalar_t, int width, typename fp8_type>
   126	__global__ std::enable_if_t<(width == 0) || !_typeConvert<scalar_t>::exists>
   127	fused_add_rms_norm_static_fp8_quant_kernel(
   128	    fp8_type* __restrict__ out,           // [..., hidden_size]
   129	    scalar_t* __restrict__ input,         // [..., hidden_size]
   130	    scalar_t* __restrict__ residual,      // [..., hidden_size]
   131	    const scalar_t* __restrict__ weight,  // [hidden_size]
   132	    const float* __restrict__ scale,      // [1]
   133	    const float epsilon, const int num_tokens, const int hidden_size) {
   134	  __shared__ float s_variance;
   135	  float variance = 0.0f;
   136	
   137	  for (int idx = threadIdx.x; idx < hidden_size; idx += blockDim.x) {
   138	    scalar_t z = input[blockIdx.x * hidden_size + idx];
   139	    z += residual[blockIdx.x * hidden_size + idx];
   140	    float x = (float)z;
   141	    variance += x * x;
   142	    residual[blockIdx.x * hidden_size + idx] = z;
   143	  }
   144	
   145	  using BlockReduce = cub::BlockReduce<float, 1024>;
   146	  __shared__ typename BlockReduce::TempStorage reduceStore;
   147	  variance = BlockReduce(reduceStore).Reduce(variance, cub::Sum{}, blockDim.x);
   148	
   149	  if (threadIdx.x == 0) {
   150	    s_variance = rsqrtf(variance / hidden_size + epsilon);
   151	  }
   152	  __syncthreads();
   153	
   154	  // invert scale to avoid division
   155	  float const scale_inv = 1.0f / *scale;
   156	
   157	  for (int idx = threadIdx.x; idx < hidden_size; idx += blockDim.x) {
   158	    float x = (float)residual[blockIdx.x * hidden_size + idx];
   159	    float const out_norm = ((scalar_t)(x * s_variance)) * weight[idx];
   160	    out[blockIdx.x * hidden_size + idx] =
   161	        scaled_fp8_conversion<true, fp8_type>(out_norm, scale_inv);
   162	  }
   163	}
