   206	void fused_add_rms_norm_static_fp8_quant(
   207	    torch::Tensor& out,       // [..., hidden_size],
   208	    torch::Tensor& input,     // [..., hidden_size]
   209	    torch::Tensor& residual,  // [..., hidden_size]
   210	    torch::Tensor& weight,    // [hidden_size]
   211	    torch::Tensor& scale,     // [1]
   212	    double epsilon) {
   213	  int hidden_size = input.size(-1);
   214	  int num_tokens = input.numel() / hidden_size;
   215	
   216	  dim3 grid(num_tokens);
   217	  /* This kernel is memory-latency bound in many scenarios.
   218	     When num_tokens is large, a smaller block size allows
   219	     for increased block occupancy on CUs and better latency
   220	     hiding on global mem ops. */
   221	  const int max_block_size = (num_tokens < 256) ? 1024 : 256;
   222	  dim3 block(std::min(hidden_size, max_block_size));
   223	  const at::cuda::OptionalCUDAGuard device_guard(device_of(input));
   224	  const cudaStream_t stream = at::cuda::getCurrentCUDAStream();
   225	  /*If the tensor types are FP16/BF16, try to use the optimized kernel
   226	    with packed + vectorized ops.
   227	    Max optimization is achieved with a width-8 vector of FP16/BF16s
   228	    since we can load at most 128 bits at once in a global memory op.
   229	    However, this requires each tensor's data to be aligned to 16
   230	    bytes.
   231	   */
   232	  auto inp_ptr = reinterpret_cast<std::uintptr_t>(input.data_ptr());
   233	  auto res_ptr = reinterpret_cast<std::uintptr_t>(residual.data_ptr());
   234	  auto wt_ptr = reinterpret_cast<std::uintptr_t>(weight.data_ptr());
   235	  bool ptrs_are_aligned =
   236	      inp_ptr % 16 == 0 && res_ptr % 16 == 0 && wt_ptr % 16 == 0;
   237	  if (ptrs_are_aligned && hidden_size % 8 == 0) {
   238	    LAUNCH_FUSED_ADD_RMS_NORM(8);
   239	  } else {
   240	    LAUNCH_FUSED_ADD_RMS_NORM(0);
   241	  }
   242	}
