   144	  int const hidden_size = input.size(-1);
   145	  int const num_tokens = input.numel() / hidden_size;
   146	  int const block_size = 256;
   147	  dim3 const grid(num_tokens);
   148	  dim3 const block(std::min(hidden_size, block_size));
   149	
   150	  const at::cuda::OptionalCUDAGuard device_guard(device_of(input));
   151	  const cudaStream_t stream = at::cuda::getCurrentCUDAStream();
   152	  VLLM_DISPATCH_FLOATING_TYPES(
   153	      input.scalar_type(),
   154	      "dynamic_per_token_scaled_fp8_quant_kernel_scalar_type", [&] {
   155	        VLLM_DISPATCH_FP8_TYPES(
   156	            out.scalar_type(),
   157	            "dynamic_per_token_scaled_fp8_quant_kernel_fp8_type", [&] {
   158	              vllm::dynamic_per_token_scaled_fp8_quant_kernel<scalar_t, fp8_t>
   159	                  <<<grid, block, 0, stream>>>(
   160	                      out.data_ptr<fp8_t>(), scales.data_ptr<float>(),
   161	                      input.data_ptr<scalar_t>(),
   162	                      scale_ub.has_value() ? scale_ub->data_ptr<float>()
   163	                                           : nullptr,
   164	                      hidden_size);
   165	            });
   166	      });
   167	}
