import time
import torch

# Prefer direct function import to avoid ambiguity
try:
    from vllm.model_executor.layers.fused_moe.moe_align_block_size import moe_align_block_size
except Exception:
    # Fallback: import module then function
    from vllm.model_executor.layers.fused_moe import moe_align_block_size as _mod
    moe_align_block_size = _mod.moe_align_block_size


def run_once(num_tokens=4096, num_experts=64, topk=2, block_size=128):
    device = 'cuda' if torch.cuda.is_available() else 'cpu'
    topk_ids = torch.randint(0, num_experts, (num_tokens * topk,), dtype=torch.int32, device=device)

    # Warmup
    if device == 'cuda':
        torch.cuda.synchronize()
    for _ in range(3):
        sorted_ids, expert_ids, num_tokens_post_pad = moe_align_block_size(
            topk_ids, block_size, num_experts
        )
        if device == 'cuda':
            torch.cuda.synchronize()

    # Benchmark
    if device == 'cuda':
        torch.cuda.synchronize()
    start = time.time()
    sorted_ids, expert_ids, num_tokens_post_pad = moe_align_block_size(
        topk_ids, block_size, num_experts
    )
    if device == 'cuda':
        torch.cuda.synchronize()
    duration = time.time() - start
    return duration, (sorted_ids, expert_ids, num_tokens_post_pad)


if __name__ == '__main__':
    dur, _ = run_once()
    print(f"Duration: {dur:.6f} s")

