    if mod is not None:
        for name in ("blockscale_fused_moe_fp8", "fused_moe_blockscale_fp8"):
            fn = getattr(mod, name, None)
            if fn is not None:
                return fn(*args, **kwargs)
    return _missing(*args, **kwargs)

