   147	  int hidden_size = input.size(-1);
   148	  int num_tokens = input.numel() / hidden_size;
   149	
   150	  dim3 grid(num_tokens);
   151	  dim3 block(std::min(hidden_size, 1024));
   152	  const at::cuda::OptionalCUDAGuard device_guard(device_of(input));
   153	  const cudaStream_t stream = at::cuda::getCurrentCUDAStream();
