   155	    vllm::rms_norm_kernel<scalar_t><<<grid, block, 0, stream>>>(
   156	        out.data_ptr<scalar_t>(), input.data_ptr<scalar_t>(),
   157	        weight.data_ptr<scalar_t>(), epsilon, num_tokens, hidden_size);
