    64	  }
    65	
    66	  __syncthreads();
    67	
    68	  // We accumulate the token counts of all experts in thread 0.
    69	  if (threadIdx.x == 0) {
    70	    cumsum[0] = 0;
    71	    for (int i = 1; i <= num_experts; ++i) {
    72	      cumsum[i] = cumsum[i - 1] +
    73	                  CEILDIV(tokens_cnts[index(num_experts, blockDim.x, i - 1)],
    74	                          block_size) *
    75	                      block_size;
    76	    }
    77	    *total_tokens_post_pad = cumsum[num_experts];
    78	  }
    79	
    80	  __syncthreads();
    81	
    82	  /**
    83	   * For each expert, each thread processes the tokens of the corresponding
    84	   * blocks and stores the corresponding expert_id for each block.
    85	   */
    86	  if (threadIdx.x < num_experts) {
    87	    for (int i = cumsum[threadIdx.x]; i < cumsum[threadIdx.x + 1];
    88	         i += block_size) {
    89	      expert_ids[i / block_size] = threadIdx.x;
    90	    }
    91	  }
    92	
    93	  /**
    94	   * Each thread processes a token shard, calculating the index of each token
    95	   * after sorting by expert number. Given the example topk_ids =
    96	   * [0,1,2,1,2,3,0,3,4] and block_size = 4, then the output would be [0, 6, *,
    97	   * *, 1, 3, *, *, 2, 4, *, *, 5, 7, *, *, 8, *, *, *], where * represents a
    98	   * padding value(preset in python).
    99	   */
   100	  for (int i = start_idx; i < numel && i < start_idx + tokens_per_thread; ++i) {
   101	    int32_t expert_id = topk_ids[i];
   102	    /** The cumsum[expert_id] stores the starting index of the tokens that the
   103	     * expert with expert_id needs to process, and
   104	     * tokens_cnts[threadIdx.x][expert_id] stores the indices of the tokens
   105	     * processed by the expert with expert_id within the current thread's token
   106	     * shard.
   107	     */
   108	    int32_t rank_post_pad =
   109	        tokens_cnts[index(num_experts, threadIdx.x, expert_id)] +
   110	        cumsum[expert_id];
   111	    sorted_token_ids[rank_post_pad] = i;
   112	    ++tokens_cnts[index(num_experts, threadIdx.x, expert_id)];
   113	  }
   114	}
   115	
   116	template <typename scalar_t, int TOPK>
   117	__global__ void moe_sum_kernel(
   118	    scalar_t* __restrict__ out,          // [..., d]
   119	    const scalar_t* __restrict__ input,  // [..., topk, d]
   120	    const int d) {
   121	  const int64_t token_idx = blockIdx.x;
   122	  for (int64_t idx = threadIdx.x; idx < d; idx += blockDim.x) {
   123	    scalar_t x = 0.0;
   124	#pragma unroll
   125	    for (int k = 0; k < TOPK; ++k) {
   126	      x += VLLM_LDG(&input[token_idx * TOPK * d + k * d + idx]);
   127	    }
   128	    out[token_idx * d + idx] = x;
   129	  }
   130	}
   131	
   132	}  // namespace moe
   133	}  // namespace vllm
   134	
   135	void moe_align_block_size(torch::Tensor topk_ids, int64_t num_experts,
   136	                          int64_t block_size, torch::Tensor sorted_token_ids,
   137	                          torch::Tensor experts_ids,
   138	                          torch::Tensor num_tokens_post_pad) {
   139	  const cudaStream_t stream = at::cuda::getCurrentCUDAStream();
   140	  VLLM_DISPATCH_INTEGRAL_TYPES(
   141	      topk_ids.scalar_type(), "moe_align_block_size_kernel", [&] {
   142	        // calc needed amount of shared mem for `tokens_cnts` and `cumsum`
   143	        // tensors
   144	        const int32_t num_thread = max((int32_t)num_experts, WARP_SIZE);
   145	        const int32_t shared_mem =
   146	            ((num_thread + 1) * num_experts + (num_experts + 1)) *
   147	            sizeof(int32_t);
   148	
   149	        // set dynamic shared mem
   150	        auto kernel = vllm::moe::moe_align_block_size_kernel<scalar_t>;
   151	        AT_CUDA_CHECK(VLLM_DevFuncAttribute_SET_MaxDynamicSharedMemorySize(
   152	            (void*)kernel, shared_mem));
   153	        kernel<<<1, num_thread, shared_mem, stream>>>(
   154	            topk_ids.data_ptr<scalar_t>(), sorted_token_ids.data_ptr<int32_t>(),
   155	            experts_ids.data_ptr<int32_t>(),
   156	            num_tokens_post_pad.data_ptr<int32_t>(), num_experts, block_size,
   157	            topk_ids.numel());
   158	      });
   159	}
   160	
   161	void moe_sum(torch::Tensor& input,   // [num_tokens, topk, hidden_size]
   162	             torch::Tensor& output)  // [num_tokens, hidden_size]
   163	{
   164	  const int hidden_size = input.size(-1);
   165	  const int num_tokens = output.numel() / hidden_size;
   166	  const int topk = input.size(1);
   167	
   168	  dim3 grid(num_tokens);
   169	  dim3 block(std::min(hidden_size, 1024));
   170	  const at::cuda::OptionalCUDAGuard device_guard(device_of(output));
   171	  const cudaStream_t stream = at::cuda::getCurrentCUDAStream();
   172	
   173	  switch (topk) {
   174	    case 2:
   175	      VLLM_DISPATCH_FLOATING_TYPES(input.scalar_type(), "moe_sum_kernel", [&] {
   176	        vllm::moe::moe_sum_kernel<scalar_t, 2><<<grid, block, 0, stream>>>(
   177	            output.data_ptr<scalar_t>(), input.data_ptr<scalar_t>(),
   178	            hidden_size);
   179	      });
   180	      break;
   181	
   182	    case 3:
   183	      VLLM_DISPATCH_FLOATING_TYPES(input.scalar_type(), "moe_sum_kernel", [&] {
   184	        vllm::moe::moe_sum_kernel<scalar_t, 3><<<grid, block, 0, stream>>>(
   185	            output.data_ptr<scalar_t>(), input.data_ptr<scalar_t>(),
   186	            hidden_size);
   187	      });
   188	      break;
   189	
   190	    case 4:
   191	      VLLM_DISPATCH_FLOATING_TYPES(input.scalar_type(), "moe_sum_kernel", [&] {
   192	        vllm::moe::moe_sum_kernel<scalar_t, 4><<<grid, block, 0, stream>>>(
   193	            output.data_ptr<scalar_t>(), input.data_ptr<scalar_t>(),
   194	            hidden_size);
   195	      });
   196	      break;
   197	
   198	    default:
   199	      at::sum_out(output, input, 1);
   200	      break;
   201	  }
   202	}
