import numpy as np
import torch

# =======================
# Determinism Setup
# =======================
def ensure_determinism():
    torch.manual_seed(42)
    np.random.seed(42)
    if torch.cuda.is_available():
        torch.cuda.manual_seed_all(42)
        torch.backends.cudnn.deterministic = True
        torch.backends.cudnn.benchmark = False
        # Disable TF32 for reproducibility unless required
        torch.backends.cuda.matmul.allow_tf32 = False
        torch.backends.cudnn.allow_tf32 = False

# =======================
# Hardware Detection
# =======================
def detect_hardware() -> Dict[str, Any]:
    hw_info = {}
    if torch.cuda.is_available():
        hw_info["device"] = "cuda"
        hw_info["device_name"] = torch.cuda.get_device_name()
        hw_info["capability"] = torch.cuda.get_device_capability()
        hw_info["memory_gb"] = torch.cuda.get_device_properties(0).total_memory / 1e9
    else:
        hw_info["device"] = "cpu"
        hw_info["device_name"] = "CPU"
        hw_info["memory_gb"] = 0
    return hw_info

# =======================
# Import Resolution
# =======================
def resolve_target() -> Tuple[Any, str]:
    """Resolve the optimization target from environment or metadata."""

    # Priority 1: Environment variables
    module_path = os.getenv("PROB_MODULE", "")
    symbol_name = os.getenv("PROB_SYMBOL", "")

    # Priority 2: Parse from commit metadata - use CpuGpuBlockAllocator
    if not (module_path and symbol_name):
        module_path = "vllm.core.block.cpu_gpu_block_allocator"
        symbol_name = "CpuGpuBlockAllocator"

    # Import with error handling
    try:
        module = importlib.import_module(module_path)
        target = module
        for attr in symbol_name.split("."):
            target = getattr(target, attr)

        fq_name = f"{module_path}.{symbol_name}"
        return target, fq_name

    except (ImportError, AttributeError) as e:
        error_data = {
            "target_resolved": False,
            "error": str(e),
            "attempted_module": module_path,
            "attempted_symbol": symbol_name
        }
        print(json.dumps(error_data))
        sys.exit(1)

# =======================
# Workload Setup
# =======================
def setup() -> Dict[str, Any]:
    """Create realistic workload for the optimization."""
    ensure_determinism()
    hw_info = detect_hardware()

    device = torch.device(hw_info["device"])
    dtype = torch.float16 if hw_info["device"] == "cuda" else torch.float32

    # Block manager configuration
    block_size = 16
    num_gpu_blocks = 1024
    num_cpu_blocks = 512

    # Sequence configuration for testing block allocation
    batch_size = 8
    seq_len = 512
    num_sequences = 4

    data = {
        "device": device,
        "dtype": dtype,
        "hw_info": hw_info,
        "block_size": block_size,
        "num_gpu_blocks": num_gpu_blocks,
        "num_cpu_blocks": num_cpu_blocks,
        "batch_size": batch_size,
        "seq_len": seq_len,
        "num_sequences": num_sequences,
        "allocator_type": "prefix_caching",  # Test the optimized prefix caching allocator
    }

    return data

# =======================
# Experiment Execution
# =======================
def experiment(data: Dict[str, Any]) -> Any:
    """Execute the optimized operation."""
    target, fq_name = resolve_target()

    # Create the block allocator
    allocator = target.create(
        allocator_type=data["allocator_type"],
        num_gpu_blocks=data["num_gpu_blocks"],
        num_cpu_blocks=data["num_cpu_blocks"],
        block_size=data["block_size"],
    )

    # Simulate block allocation patterns
    results = {
        "allocated_blocks": [],
        "num_free_gpu": [],
        "num_free_cpu": []
    }

    # Test allocation of mutable and immutable blocks
    allocated_blocks = []

    # Allocate some mutable blocks
    for i in range(data["num_sequences"]):
        try:
            block = allocator.allocate_mutable_block(
                prev_block=None,
                device=data["device"]
            )
            allocated_blocks.append(block)
            results["allocated_blocks"].append(block.block_id if hasattr(block, 'block_id') else i)
        except AttributeError:
            # Fallback for old API
            block = allocator.allocate_mutable(
                prev_block=None,
                device=data["device"]
            )
            allocated_blocks.append(block)
            results["allocated_blocks"].append(block.block_id if hasattr(block, 'block_id') else i)

    # Check free blocks
    from vllm.config import Device
    results["num_free_gpu"].append(allocator.get_num_free_blocks(Device.GPU))
    results["num_free_cpu"].append(allocator.get_num_free_blocks(Device.CPU))

    # Test immutable block allocation with token IDs
    token_ids = list(range(data["block_size"]))
    for i in range(data["num_sequences"] // 2):
        try:
            block = allocator.allocate_immutable_block(
                prev_block=None,
                token_ids=token_ids,
                device=data["device"]
            )
            allocated_blocks.append(block)
        except AttributeError:
            # Fallback for old API
            block = allocator.allocate_immutable(
                prev_block=None,
                token_ids=token_ids,
                device=data["device"]
            )
            allocated_blocks.append(block)

    results["num_free_gpu"].append(allocator.get_num_free_blocks(Device.GPU))

    # Free some blocks
    for block in allocated_blocks[:len(allocated_blocks)//2]:
        allocator.free(block)

    results["num_free_gpu"].append(allocator.get_num_free_blocks(Device.GPU))

    return results

# =======================
# Result I/O
# =======================
def store_result(result: Any, filepath: str) -> None:
    """Store result for reference comparison."""
    if isinstance(result, torch.Tensor):
        torch.save({"type": "tensor", "data": result.cpu()}, filepath)
    else:
        torch.save({"type": "generic", "data": result}, filepath)

def load_result(filepath: str) -> Any:
    """Load reference result."""
    data = torch.load(filepath)
    return data.get("data", data)

# =======================
# Equivalence Checking
# =======================
def check_equivalence(current_result: Any, reference_result: Any) -> None:
    """Verify functional equivalence."""
    if isinstance(current_result, dict) and isinstance(reference_result, dict):
        # Check that the same keys exist
        assert set(current_result.keys()) == set(reference_result.keys()), \
            f"Keys mismatch: {current_result.keys()} vs {reference_result.keys()}"

        # For block allocation, we mainly care about the number of free blocks
        # being consistent after operations
        if "num_free_gpu" in current_result:
            # Check final state is equivalent
            assert current_result["num_free_gpu"][-1] == reference_result["num_free_gpu"][-1], \
                f"Final GPU free blocks mismatch: {current_result['num_free_gpu'][-1]} vs {reference_result['num_free_gpu'][-1]}"

# =======================
# Timing Implementation
# =======================
def time_gpu(func, warmup=5, iterations=50) -> Tuple[Any, Dict[str, float]]:
    """Time GPU operations with CUDA events."""
    # Warmup
    for _ in range(warmup):
        _ = func()
        if torch.cuda.is_available():
            torch.cuda.synchronize()

    # Timing
    times_ms = []
    for _ in range(iterations):
        if torch.cuda.is_available():
            start = torch.cuda.Event(enable_timing=True)
            end = torch.cuda.Event(enable_timing=True)

            torch.cuda.synchronize()
            start.record()
            result = func()
            end.record()
            torch.cuda.synchronize()

            times_ms.append(start.elapsed_time(end))
        else:
            start = time.perf_counter()
            result = func()
            end = time.perf_counter()
            times_ms.append((end - start) * 1000)

    # Statistics
    times_ms.sort()
    stats = {
        "avg_ms": sum(times_ms) / len(times_ms),
        "p50_ms": times_ms[len(times_ms) // 2],
        "p95_ms": times_ms[int(len(times_ms) * 0.95)],
        "p99_ms": times_ms[int(len(times_ms) * 0.99)],
        "min_ms": times_ms[0],
        "max_ms": times_ms[-1],
        "std_ms": np.std(times_ms)
    }

    return result, stats

# =======================
# Main Test Function
# =======================
def run_test(eqcheck: bool = False, reference: bool = False, prefix: str = '') -> float:
    """Main test entry point."""

    # Setup
    data = setup()
    hw_info = data["hw_info"]

    # Timing
    if hw_info["device"] == "cuda":
        warmup = 5
        iters = 50
    else:
        warmup = 3
        iters = 10

    result, timing_stats = time_gpu(lambda: experiment(data), warmup=warmup, iterations=iters)
    avg_ms = timing_stats["avg_ms"]
    p50_ms = timing_stats["p50_ms"]
    p95_ms = timing_stats["p95_ms"]

    # Reference handling
    commit_hash = os.getenv("COMMIT_HASH", "3476ed0809ec91a3457da0cb90543133a4f4b519")
    impl_tag = os.getenv("IMPL_TAG", "child")
    ref_file = f"{prefix}_{impl_tag}_{commit_hash}_reference.pt"

    if reference:
        store_result(result, ref_file)

    if eqcheck and os.path.exists(ref_file):
        ref_result = load_result(ref_file)
        check_equivalence(result, ref_result)

    # Output compact JSON schema
    summary = {
        "impl_tag": impl_tag,
        "commit_hash": commit_hash,
        "device": str(hw_info["device"]),
        "dtype": str(data["dtype"]),
        "iters": iters,
        "warmup": warmup,
        "avg_ms": avg_ms,
        "p50_ms": p50_ms,
        "p95_ms": p95_ms,
        "eq_level": os.getenv("PROB_EQ_LEVEL", "behavioral"),
        "opt_path_hit": True
    }
    print(json.dumps(summary))

    return avg_ms / 1000.0

# =======================
# Entry Point
# =======================
if __name__ == "__main__":
    import argparse
    parser = argparse.ArgumentParser()
    parser.add_argument("--eqcheck", action="store_true")
    parser.add_argument("--reference", action="store_true")
    parser.add_argument("--prefix", type=str, default="")
    args = parser.parse_args()

    run_test(args.eqcheck, args.reference, args.prefix)
